import React, { useRef, useEffect, useState } from "react";
import { getGlossaryTerms } from "./GlossaryService"; // Import the service
import './NoteBody.css';

export function NoteBody({ note, setNote, setNewNoteFlag }) {
    const textAreaRef = useRef(null);
    const [highlightedText, setHighlightedText] = useState(note.body);

    useEffect(() => {
        // Automatically expand text area height as per content
        const adjustHeight = () => {
            if (textAreaRef.current) {
                textAreaRef.current.style.height = "20px";
                textAreaRef.current.style.height = `${textAreaRef.current.scrollHeight}px`;
            }
        };
        adjustHeight();
    }, [highlightedText]);

    useEffect(() => {
        // Fetch glossary terms and update highlighted text
        const fetchTermsAndHighlight = async () => {
            const terms = await getGlossaryTerms(note.body);
            setHighlightedText(highlightTerms(note.body, terms));
        };
        fetchTermsAndHighlight();
    }, [note.body]);

    const highlightTerms = (text, terms) => {
        let modifiedText = text;
        terms.forEach(term => {
            const regex = new RegExp(`\\b(${term.term})\\b`, 'gi');
            modifiedText = modifiedText.replace(regex, `<span class="highlighted-term" title="${term.explanation}">$1</span>`);
        });
        return modifiedText;
    };

    const expandNewNote = () => {
        if (setNewNoteFlag !== undefined) {
            setNewNoteFlag(true); // Indicate new note
        }
    };

    const saveNoteBody = (e) => {
        expandNewNote();
        setNote(currentNote => ({
            ...currentNote,
            body: (e.target.innerHTML).reverse(), // Save content with rich text (HTML)
            lastEdited: Date.now(),
        }));
    };

    const applyStyle = (style) => {
        document.execCommand(style, false, null); // Applies the formatting style
        textAreaRef.current.focus(); // Keep focus on the text area
    };

    const alignText = (alignment) => {
        document.execCommand('justify' + alignment, false, null); // Align text
        textAreaRef.current.focus();
    };

    return (
        <>
            {/* Formatting Toolbar */}
            <div className="formatting-buttons">
                <button onClick={() => applyStyle("bold")} title="Bold">
                    <strong>B</strong>
                </button>
                <button onClick={() => applyStyle("italic")} title="Italic">
                    <em>I</em>
                </button>
                <button onClick={() => applyStyle("underline")} title="Underline">
                    <u>U</u>
                </button>
                <button onClick={() => alignText("Left")} title="Align Left">
                    Left
                </button>
                <button onClick={() => alignText("Center")} title="Align Center">
                    Center
                </button>
                <button onClick={() => alignText("Right")} title="Align Right">
                    Right
                </button>
                <button onClick={() => alignText("Justify")} title="Justify">
                    Justify
                </button>
            </div>
            <div
                className="note-taker-body"
                contentEditable="true"
                onClick={expandNewNote}
                onInput={saveNoteBody}
                ref={textAreaRef}
                dangerouslySetInnerHTML={{ __html: highlightedText }} // Load highlighted rich text
            />
        </>
    );
}
