import React, { useEffect, useState } from "react";
import { getGlossaryTerms } from "./GlossaryService"; // Import the service
import './NoteBody.css';

export function NoteBody({ note, setNote, setNewNoteFlag }) {
    const [text, setText] = useState(note.body);
    const [highlightedText, setHighlightedText] = useState(note.body);

    useEffect(() => {
        // Fetch glossary terms and update highlighted text
        const fetchTermsAndHighlight = async () => {
            const terms = await getGlossaryTerms(text);
            setHighlightedText(highlightTerms(text, terms));
        };
        fetchTermsAndHighlight();
    }, [text]);

    function highlightTerms(text, terms) {
        let modifiedText = text;
        terms.forEach(term => {
            const regex = new RegExp(`\\b(${term.term})\\b`, 'gi');
            modifiedText = modifiedText.replace(regex, `<span class="highlighted-term" title="${term.explanation}">$1</span>`);
        });
        return modifiedText;
    }

    function expandNewNote() {
        if (setNewNoteFlag !== undefined) {
            setNewNoteFlag(true); // Indicate new note
        }
    }

    function saveNoteBody() {
        expandNewNote();
        setNote((currentNote) => ({
            ...currentNote,
            body: text.split('').reverse().join(''), // Reverse text before saving
            lastEdited: Date.now(),
        }));
    }

    function handleInput(e) {
        setText(e.target.innerHTML.split('').reverse().join('')); // Reverse input as user types
    }

    function applyStyle(style) {
        document.execCommand(style, false, null); // Apply formatting style
        document.getElementById("noteBody").focus(); // Keep focus on the note body
    }

    return (
        <>
            {/* Formatting Toolbar */}
            <div className="formatting-buttons">
                <button onClick={() => applyStyle("bold")} title="Bold">
                    <strong>B</strong>
                </button>
                <button onClick={() => applyStyle("italic")} title="Italic">
                    <em>I</em>
                </button>
                <button onClick={() => applyStyle("underline")} title="Underline">
                    <u>U</u>
                </button>
            </div>
            <div
                id="noteBody"
                className="note-taker-body"
                contentEditable="true"
                onClick={expandNewNote}
                onInput={handleInput}
                dangerouslySetInnerHTML={{ __html: highlightedText.split('').reverse().join('') }} // Display reversed text
                onBlur={saveNoteBody} // Save on blur
            />
        </>
    );
}
