import React, { useRef, useEffect } from "react";

export function NoteBody({ note, setNote, setNewNoteFlag }) {
    let textAreaRef = useRef(null);

    useEffect(() => {
        if (textAreaRef.current) {
            textAreaRef.current.style.height = "20px";
            textAreaRef.current.style.height = textAreaRef.current.scrollHeight + "px";
        }
    }, [note]);

    function expandNewNote() {
        if (setNewNoteFlag !== undefined) {
            setNewNoteFlag(true); // Indicate new note
        }
    }

    function saveNoteBody(e) {
        expandNewNote();
        setNote((currentNote) => ({
            ...currentNote,
            body: e.target.innerHTML, // Save content with rich text (HTML)
            lastEdited: Date.now(),
        }));
    }

    function applyStyle(style) {
        document.execCommand(style, false, null); // Applies the formatting style
    }

    const placeholder = "Write your text...";

    return (
        <>
            <div className="formatting-buttons">
                <button onClick={() => applyStyle("bold")}>Bold</button>
                <button onClick={() => applyStyle("italic")}>Italic</button>
                <button onClick={() => applyStyle("underline")}>Underline</button>
            </div>
            <div
                className="note-taker-body"
                contentEditable="true" // Enable rich text editing
                onClick={expandNewNote}
                onInput={saveNoteBody} // Use onInput for contentEditable
                ref={textAreaRef}
                dangerouslySetInnerHTML={{
                    __html: note.body === "" ? `<span style="color: #888;">${placeholder}</span>` : note.body,
                }}
                style={{
                    minHeight: "40px",
                    padding: "10px",
                    //border: "1px solid #ccc",
                    whiteSpace: "pre-wrap",
                    overflowWrap: "break-word",
                    width: "100%", // Responsive width
                    boxSizing: "border-box", // Include padding and border in element's total width
                    color: note.body === "" ? "#888" : "#ccc", // Placeholder color
                    //textAlign: "left", // Ensure left alignment
                    //direction: "ltr", // Left-to-Right text direction
                }}
                // onFocus={(e) => {
                //     // Clear placeholder on focus
                //     if (note.body === "") {
                //         e.target.innerHTML = "";
                //         e.target.style.color = "#000"; // Change color to black
                //     }
                // }}
                // onBlur={(e) => {
                //     // Restore placeholder if empty
                //     if (e.target.innerHTML.trim() === "") {
                //         e.target.innerHTML = `<span style="color: #888;">${placeholder}</span>`;
                //         e.target.style.color = "#888"; // Placeholder color
                //     }
                // }}
            />
        </>
    );
}
