import React, { useRef, useEffect } from "react";

export function NoteBody({ note, setNote, setNewNoteFlag }) {
    let textAreaRef = useRef(null);

    useEffect(() => {
        if (textAreaRef) {
            textAreaRef.style.height = "20px";
            textAreaRef.style.height = textAreaRef.scrollHeight + "px";
        }
    }, [note]);

    function expandNewNote() {
        if (setNewNoteFlag !== undefined) {
            // i.e component is being used in New Note
            setNewNoteFlag(true);
        }
    }

    function saveNoteBody(e) {
        expandNewNote();
        setNote((currentNote) => ({
            ...currentNote,
            body: e.target.value,
            lastEdited: Date.now(),
        }));
    }

    function applyStyle(style) {
        document.execCommand(style, false, null); // Applies the formatting style
    }

    return (
        <div>
        {/* Formatting Buttons */}
        <div className="formatting-buttons">
            <button onClick={() => applyStyle("bold")}>Bold</button>
            <button onClick={() => applyStyle("italic")}>Italic</button>
            <button onClick={() => applyStyle("underline")}>Underline</button>
        </div>
        <div
            className="note-taker-body"
            placeholder="Take a note..."
            type="text"
            contentEditable="true"
            autoFocus
            onClick={expandNewNote}
            onChange={saveNoteBody}
            ref={(ref) => (textAreaRef = ref)}
                            dangerouslySetInnerHTML={{ __html: note.body }} // Load saved rich text

            value={note.body}
        />
        </div>
    );
}
