import React, { useRef, useEffect } from "react";
import './NoteBody.css'; // Import the CSS file

export function NoteBody({ note, setNote, setNewNoteFlag }) {
    const textAreaRef = useRef(null);
    const cursorPositionRef = useRef(null); // Track cursor position

    useEffect(() => {
        if (textAreaRef.current) {
            textAreaRef.current.style.height = "20px";
            textAreaRef.current.style.height = textAreaRef.current.scrollHeight + "px";
        }
    }, [note]);

    function expandNewNote() {
        if (setNewNoteFlag !== undefined) {
            setNewNoteFlag(true); // Indicate new note
        }
    }

    // Save the current selection (cursor) position
    function saveSelection() {
        const selection = window.getSelection();
        if (selection.rangeCount > 0) {
            cursorPositionRef.current = selection.getRangeAt(0);
        }
    }

    // Restore the selection (cursor) position
    function restoreSelection() {
        const selection = window.getSelection();
        if (cursorPositionRef.current) {
            selection.removeAllRanges();
            selection.addRange(cursorPositionRef.current);
        }
    }

    function saveNoteBody(e) {
        expandNewNote();
        saveSelection(); // Save the cursor position before updating content
        setNote((currentNote) => ({
            ...currentNote,
            body: e.target.innerHTML, // Save content with rich text (HTML)
            lastEdited: Date.now(),
        }));
        setTimeout(restoreSelection, 0); // Restore cursor after state update
    }

    function applyStyle(style) {
        document.execCommand(style, false, null); // Applies the formatting style
        textAreaRef.current.focus(); // Keep focus on the text area
        saveSelection(); // Save the cursor position after styling
    }

    return (
        <>
            {/* Formatting Toolbar */}
            <div className="formatting-buttons">
                <button onClick={() => applyStyle("bold")} title="Bold">
                    <strong>B</strong>
                </button>
                <button onClick={() => applyStyle("italic")} title="Italic">
                    <em>I</em>
                </button>
                <button onClick={() => applyStyle("underline")} title="Underline">
                    <u>U</u>
                </button>
            </div>
            <div
                className="note-taker-body"
                contentEditable="true" // Enable rich text editing
                onClick={expandNewNote}
                onInput={saveNoteBody} // Use onInput to capture changes
                ref={textAreaRef}
                onKeyUp={saveSelection} // Save the selection on key up
                onMouseUp={saveSelection} // Save the selection on mouse up
                dangerouslySetInnerHTML={{ __html: note.body }} // Load saved rich text
            />
        </>
    );
}
